import React from "react";

import styled from "styled-components";

const Aside = styled.aside`
  width: 200px;
  background: #3b0a0a;
  color: white;
  padding: 1rem;
`;

const SidebarItem = styled.div`
  font-weight: bold;
  background: #000;
  padding: 0.5rem;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background: #8b0000;
  }
`;



export default function CadastroProduto() {
  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#2c2c2c" }}>
      <aside style={{ width: "200px", background: "#3b0a0a", color: "white", padding: "1rem" }}>
        <div style={{ textAlign: "center", marginBottom: "1rem" }}>
          <img src="/MaleUser.svg" alt="Perfil" style={{ width: "60px" }} />
        </div>
        <div style={{
    fontWeight: "bold",
    background: "#000",
    padding: "0.5rem",
    borderRadius: "5px",
    cursor: "pointer"
  }}
  onMouseEnter={e => (e.currentTarget.style.background = "#8b0000")}
  onMouseLeave={e => (e.currentTarget.style.background = "#000")}>
          Cadastro de produtos
        </div>
      </aside>
      <main style={{ flex: 1, padding: "2rem", background: "#4a4a4a", color: "white" }}>
        <h2>Cadastro de Produtos</h2>
        <form style={{ marginTop: "1rem", display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
          <input placeholder="Nome" style={{ padding: "0.5rem" }} />
          <input placeholder="Categoria" style={{ padding: "0.5rem" }} />
          <input placeholder="Descrição" style={{ padding: "0.5rem" }} />
          <input placeholder="Preço" style={{ padding: "0.5rem" }} />
          <div style={{ gridColumn: "span 2" }}>
            <label style={{ display: "block", marginBottom: "0.5rem" }}>Imagem do produto</label>
            <input type="file" />
          </div>
        </form>
      </main>
    </div>
  );
}
