import React from "react";

import styled from "styled-components";

import { useNavigate } from "react-router-dom";

const Aside = styled.aside`
  width: 200px;
  background: #3b0a0a;
  color: white;
  padding: 1rem;
`;

const SidebarItem = styled.div`
  font-weight: bold;
  background: #000;
  padding: 0.5rem;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background: #8b0000;
  }
`;

const LinhaTitulo = styled.div`
  width: 100%;
  height: 4px;
  background: linear-gradient(to right, #fff, #777);
  margin-top: 0.5rem;
  margin-bottom: 1.5rem;
  border: none;
  border-radius: 2px;
`;

const Fundo = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  height: 100vh;
  width: 100vw;
  background-image: url("/sushi.png");
  background-size: cover;
  background-position: center;
  z-index: 0;
`;

const Sobreposicao = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  height: 100vh;
  width: 100vw;
  background-image: url("/sheerwhite.png");
  background-size: cover;
  background-position: center;
  opacity: 0.9;
  z-index: 1;
`;

const Conteudo = styled.div`
  position: relative;
  z-index: 2;
  height: 100vh;
  display: flex;
`;




export default function CadastroProduto() {
  const navigate = useNavigate();
  return (
    <>
      <Fundo />
      <Sobreposicao />
      <Conteudo>
        <Aside>
          <div style={{ textAlign: "center", marginBottom: "1rem" }}>
            <img src="/MaleUser.svg" alt="Perfil" style={{ width: "60px" }} />
          </div>
        <SidebarItem>Cadastro de produtos</SidebarItem>
        <SidebarItem onClick={() => navigate("/relatorios")}>
            Relatórios
        </SidebarItem>

<SidebarItem onClick={() => navigate("/clientes")}>
    Clientes
</SidebarItem>

<SidebarItem onClick={() => navigate("/registro-pedido")}>
    Registro de pedido
</SidebarItem>

<SidebarItem onClick={() => navigate("/cadastro-clientes")}>
    Cadastro de clientes
</SidebarItem>
        </Aside>

        <main style={{ flex: 1, padding: "2rem", color: "white" }}>
          <h2>Cadastro de Produtos</h2>
          <LinhaTitulo />
          <form style={{ marginTop: "1rem", display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
            <input placeholder="Nome" style={{ padding: "0.5rem" }} />
            <input placeholder="Categoria" style={{ padding: "0.5rem" }} />
            <input placeholder="Descrição" style={{ padding: "0.5rem" }} />
            <input placeholder="Preço" style={{ padding: "0.5rem" }} />
            <div style={{ gridColumn: "span 2" }}>
              <label style={{ display: "block", marginBottom: "0.5rem" }}>Imagem do produto</label>
              <input type="file" />
            </div>
          </form>
        </main>
      </Conteudo>
    </>
  );
}
